import { Component } from '@angular/core';

@Component({
  selector: 'main-menu',
  templateUrl: './main-menu.component.html',
  styleUrls: ['./main-menu.component.css']
})
export class MainMenuComponent {
  mainMenuItem:string[];

  constructor(){
    this.mainMenuItem = ['Home','Products','About','Contact'];
    console.log(this.mainMenuItem[0]);

  }


}
<div class="ekart-menu">
    <a *ngFor="let menu of mainMenuItem"></a>
</div>
