import { Component } from '@angular/core';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {
  searchText: string = '';

  updateSearchText(event: any){
      this.searchText = event.target.value;
  }
}
<div class="ekart--search--product">
    <input class="ekart-search-product-input" [(ngModel)]="searchText">
    <button [ngClass]="{'btn':true,'btn-search':searchText}"[disabled]="!searchText" >Search</button>
</div>
<div class="ekart-search-result-for" *ngIf="searchText !=''"><p><strong>Search result for:</strong> {{ searchText }}</p></div>--
